    <!-- Main Content -->
    <div class="checkout-content" *ngIf="!showSuccess">
      <div class="checkout-main">
        <h1>Finalizar Compra</h1>

        <!-- Delivery Information -->
        <div class="section-card">
          <h2>ðŸšš InformaciÃ³n de Entrega</h2>
          <form [formGroup]="checkoutForm">
            <div class="form-group">
              <label for="direccion">DirecciÃ³n de Entrega *</label>
              <input 
                type="text" 
                id="direccion"
                formControlName="direccion"
                placeholder="Calle, nÃºmero, distrito"
                class="form-control"
                [class.error]="checkoutForm.get('direccion')?.invalid && checkoutForm.get('direccion')?.touched">
              <span class="error-message" *ngIf="checkoutForm.get('direccion')?.invalid && checkoutForm.get('direccion')?.touched">
                La direcciÃ³n es obligatoria (mÃ­n 10 caracteres)
              </span>
            </div>

            <div class="form-group">
