import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { PedidoService } from '../../services/pedido.service';
import { PlatoService } from '../../services/plato.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  vistaActual = 'pedidos'; // 'pedidos' o 'platos'
  pedidos: any[] = [];
  loading = false;

  // Formulario de nuevo plato
  nuevoPlato = {
    nombre: '',
    descripcion: '',
    precio: 0,
    restauranteId: 1,
    imagen: '',
    categoria: '',
    disponible: true,
    macros: {
      calorias: 0,
      proteinas: 0,
      carbohidratos: 0,
      grasas: 0
    },
    etiquetas: [] as string[]
  };

  etiquetasDisponibles = [
    'Vegano',
    'Vegetariano',
    'Sin Gluten',
    'Bajo en Calorías',
    'Alto en Proteínas',
    'Keto',
    'Paleo',
    'Sin Lácteos',
    'Orgánico'
  ];

  constructor(
    private authService: AuthService,
    private pedidoService: PedidoService,
    private platoService: PlatoService,
    private router: Router
  ) {}

  ngOnInit() {
    const user = this.authService.getCurrentUser();
    if (!user || user.role !== 'admin') {
      alert('Acceso denegado. Solo administradores.');
      this.router.navigate(['/']);
      return;
    }
    this.cargarPedidos();
  }

  cambiarVista(vista: string) {
    this.vistaActual = vista;
    if (vista === 'pedidos') {
      this.cargarPedidos();
    }
  }

  cargarPedidos() {
    this.loading = true;
    this.pedidoService.obtenerTodosPedidos().subscribe({
      next: (data: any) => {
        this.pedidos = data;
        this.loading = false;
      },
      error: (err: any) => {
        console.error('Error al cargar pedidos:', err);
        this.loading = false;
      }
    });
  }

  cambiarEstadoPedido(pedidoId: number, nuevoEstado: string) {
    if (!confirm(`¿Cambiar estado del pedido #${pedidoId} a "${nuevoEstado}"?`)) {
      return;
    }

    this.pedidoService.actualizarEstadoPedido(pedidoId, nuevoEstado).subscribe({
      next: () => {
        alert('Estado actualizado correctamente');
        this.cargarPedidos();
      },
      error: (err: any) => {
        alert('Error al actualizar estado: ' + err.error.error);
      }
    });
  }

  toggleEtiqueta(etiqueta: string) {
    const index = this.nuevoPlato.etiquetas.indexOf(etiqueta);
    if (index > -1) {
      this.nuevoPlato.etiquetas.splice(index, 1);
    } else {
      this.nuevoPlato.etiquetas.push(etiqueta);
    }
  }

  tieneEtiqueta(etiqueta: string): boolean {
    return this.nuevoPlato.etiquetas.includes(etiqueta);
  }

  agregarPlato() {
    if (!this.nuevoPlato.nombre || !this.nuevoPlato.descripcion || this.nuevoPlato.precio <= 0) {
      alert('Por favor completa todos los campos obligatorios');
      return;
    }

    this.loading = true;
    this.platoService.crearPlato(this.nuevoPlato).subscribe({
      next: (response: any) => {
        alert('Plato agregado correctamente');
        this.limpiarFormulario();
        this.loading = false;
      },
      error: (err: any) => {
        alert('Error al agregar plato: ' + err.error.error);
        this.loading = false;
      }
    });
  }

  limpiarFormulario() {
    this.nuevoPlato = {
      nombre: '',
      descripcion: '',
      precio: 0,
      restauranteId: 1,
      imagen: '',
      categoria: '',
      disponible: true,
      macros: {
        calorias: 0,
        proteinas: 0,
        carbohidratos: 0,
        grasas: 0
      },
      etiquetas: []
    };
  }

  getEstadoClass(estado: string): string {
    const classes: any = {
      'pendiente': 'badge-warning',
      'confirmado': 'badge-info',
      'preparando': 'badge-primary',
      'en_camino': 'badge-success',
      'entregado': 'badge-success',
      'cancelado': 'badge-danger'
    };
    return classes[estado] || 'badge-secondary';
  }

  formatearFecha(fecha: string): string {
    if (!fecha) return 'N/A';
    const date = new Date(fecha);
    if (isNaN(date.getTime())) return 'N/A';
    return date.toLocaleString('es-ES');
  }
}
<div class="admin-container">
  <div class="admin-sidebar">
    <h2>🛠️ Panel Admin</h2>
    <nav class="admin-nav">
      <button 
        [class.active]="vistaActual === 'pedidos'" 
        (click)="cambiarVista('pedidos')">
        📦 Gestionar Pedidos
      </button>
      <button 
        [class.active]="vistaActual === 'platos'" 
        (click)="cambiarVista('platos')">
        🍽️ Agregar Platos
      </button>
    </nav>
  </div>

  <div class="admin-content">
    <!-- Vista de Gestión de Pedidos -->
    <div *ngIf="vistaActual === 'pedidos'" class="pedidos-section">
      <h1>Gestión de Pedidos</h1>
      
      <div *ngIf="loading" class="loading">Cargando pedidos...</div>

      <div *ngIf="!loading && pedidos.length === 0" class="empty-state">
        No hay pedidos para gestionar
      </div>

      <div *ngIf="!loading && pedidos.length > 0" class="pedidos-grid">
        <div *ngFor="let pedido of pedidos" class="pedido-card">
          <div class="pedido-header">
            <h3>Pedido #{{ pedido.id }}</h3>
            <span class="badge" [ngClass]="getEstadoClass(pedido.estado)">
              {{ pedido.estado }}
            </span>
          </div>

          <div class="pedido-info">
            <p><strong>Cliente ID:</strong> {{ pedido.usuarioId }}</p>
            <p><strong>Fecha:</strong> {{ formatearFecha(pedido.fechaPedido) }}</p>
            <p><strong>Total:</strong> S/{{ pedido.total.toFixed(2) }}</p>
            <p><strong>Dirección:</strong> {{ pedido.direccionEntrega }}</p>
            <p *ngIf="pedido.telefono"><strong>Teléfono:</strong> {{ pedido.telefono }}</p>
            <p *ngIf="pedido.metodoPago"><strong>Método Pago:</strong> {{ pedido.metodoPago }}</p>
            <p *ngIf="pedido.notas"><strong>Notas:</strong> {{ pedido.notas }}</p>
          </div>

          <div class="pedido-items">
            <h4>Items:</h4>
            <ul>
              <li *ngFor="let item of pedido.items">
                {{ item.cantidad }}x {{ item.nombre }} - S/{{ item.precio.toFixed(2) }}
              </li>
            </ul>
          </div>

          <div class="pedido-actions">
            <button 
              *ngIf="pedido.estado === 'pendiente'" 
              class="btn btn-success"
              (click)="cambiarEstadoPedido(pedido.id, 'confirmado')">
              ✅ Confirmar
            </button>
            <button 
              *ngIf="pedido.estado === 'confirmado'" 
              class="btn btn-primary"
              (click)="cambiarEstadoPedido(pedido.id, 'preparando')">
              👨‍🍳 Preparando
            </button>
            <button 
              *ngIf="pedido.estado === 'preparando'" 
              class="btn btn-info"
              (click)="cambiarEstadoPedido(pedido.id, 'en_camino')">
              🚚 En Camino
            </button>
            <button 
              *ngIf="pedido.estado === 'en_camino'" 
              class="btn btn-success"
              (click)="cambiarEstadoPedido(pedido.id, 'entregado')">
              ✅ Entregado
            </button>
            <button 
              *ngIf="['pendiente', 'confirmado', 'preparando'].includes(pedido.estado)" 
              class="btn btn-danger"
              (click)="cambiarEstadoPedido(pedido.id, 'cancelado')">
              ❌ Cancelar
            </button>
          </div>
        </div>
      </div>
    </div>

    <!-- Vista de Agregar Platos -->
    <div *ngIf="vistaActual === 'platos'" class="platos-section">
      <h1>Agregar Nuevo Plato</h1>

      <form class="plato-form" (submit)="agregarPlato(); $event.preventDefault()">
        <div class="form-section">
          <h3>Información Básica</h3>
          
          <div class="form-group">
            <label>Nombre del Plato *</label>
            <input 
              type="text" 
              [(ngModel)]="nuevoPlato.nombre" 
              name="nombre"
              placeholder="Ej: Ensalada César"
              required>
          </div>

          <div class="form-group">
            <label>Descripción *</label>
            <textarea 
              [(ngModel)]="nuevoPlato.descripcion" 
              name="descripcion"
              rows="3"
              placeholder="Describe el plato..."
              required></textarea>
          </div>

          <div class="form-row">
            <div class="form-group">
              <label>Precio (S/) *</label>
              <input 
                type="number" 
                [(ngModel)]="nuevoPlato.precio" 
                name="precio"
                min="0" 
                step="0.01"
                required>
            </div>

            <div class="form-group">
              <label>Categoría</label>
              <input 
                type="text" 
                [(ngModel)]="nuevoPlato.categoria" 
                name="categoria"
                placeholder="Ej: Ensaladas">
            </div>
          </div>

          <div class="form-group">
            <label>URL de Imagen</label>
            <input 
              type="url" 
              [(ngModel)]="nuevoPlato.imagen" 
              name="imagen"
              placeholder="https://ejemplo.com/imagen.jpg">
          </div>
        </div>

        <div class="form-section">
          <h3>Información Nutricional</h3>
          
          <div class="form-row">
            <div class="form-group">
              <label>Calorías (kcal)</label>
              <input 
                type="number" 
                [(ngModel)]="nuevoPlato.macros.calorias" 
                name="calorias"
                min="0">
            </div>

            <div class="form-group">
              <label>Proteínas (g)</label>
              <input 
                type="number" 
                [(ngModel)]="nuevoPlato.macros.proteinas" 
                name="proteinas"
                min="0" 
                step="0.1">
            </div>
          </div>

          <div class="form-row">
            <div class="form-group">
              <label>Carbohidratos (g)</label>
              <input 
                type="number" 
                [(ngModel)]="nuevoPlato.macros.carbohidratos" 
                name="carbohidratos"
                min="0" 
                step="0.1">
            </div>

            <div class="form-group">
              <label>Grasas (g)</label>
              <input 
                type="number" 
                [(ngModel)]="nuevoPlato.macros.grasas" 
                name="grasas"
                min="0" 
                step="0.1">
            </div>
          </div>
        </div>

        <div class="form-section">
          <h3>Etiquetas / Características</h3>
          
          <div class="etiquetas-grid">
            <label 
              *ngFor="let etiqueta of etiquetasDisponibles" 
              class="etiqueta-checkbox">
              <input 
                type="checkbox" 
                [checked]="tieneEtiqueta(etiqueta)"
                (change)="toggleEtiqueta(etiqueta)">
              <span>{{ etiqueta }}</span>
            </label>
          </div>
        </div>

        <div class="form-section">
          <div class="form-group">
            <label class="checkbox-label">
              <input 
                type="checkbox" 
                [(ngModel)]="nuevoPlato.disponible" 
                name="disponible">
              <span>Plato disponible</span>
            </label>
          </div>
        </div>

        <div class="form-actions">
          <button type="button" class="btn btn-secondary" (click)="limpiarFormulario()">
            Limpiar
          </button>
          <button type="submit" class="btn btn-primary" [disabled]="loading">
            {{ loading ? 'Guardando...' : '✅ Agregar Plato' }}
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
