  styleUrls: ['./restaurantes.component.css']
})
export class RestaurantesComponent implements OnInit {
  restaurantes: Restaurante[] = [];
  restaurantesFiltrados: Restaurante[] = [];
  categorias: string[] = ['Todas', 'Vegano', 'Alto en Prote√≠na', 'Keto', 'Balanced', 'Low Calorie', 'Paleo', 'Detox', 'Mediterr√°nea', 'Ensaladas', 'Asi√°tica Saludable'];
  categoriaSeleccionada: string = 'Todas';
  searchTerm: string = '';
  ordenamiento: string = 'calificacion';
  loading: boolean = true;

  constructor(
    private restauranteService: RestauranteService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.cargarRestaurantes();
  }

  cargarRestaurantes(): void {
    this.loading = true;
    this.restauranteService.getRestaurantes().subscribe({
      next: (data) => {
        this.restaurantes = data;
        this.aplicarFiltros();
        this.loading = false;
      },
      error: (err) => {
        console.error('Error al cargar restaurantes:', err);
        this.loading = false;
      }
    });
  }

  aplicarFiltros(): void {
    let resultado = [...this.restaurantes];

    // Filtrar por categor√≠a
    if (this.categoriaSeleccionada !== 'Todas') {
      resultado = resultado.filter(r => r.especialidad === this.categoriaSeleccionada);
    }

    // Filtrar por b√∫squeda
    if (this.searchTerm.trim()) {
      const term = this.searchTerm.toLowerCase();
      resultado = resultado.filter(r =>
        r.nombre.toLowerCase().includes(term) ||
        r.descripcion.toLowerCase().includes(term) ||
        r.especialidad.toLowerCase().includes(term)
      );
    }

    // Ordenar
    switch (this.ordenamiento) {
      case 'calificacion':
        resultado.sort((a, b) => b.calificacion - a.calificacion);
        break;
      case 'tiempo':
        resultado.sort((a, b) => {
          const tiempoA = parseInt(a.tiempoEntrega.split('-')[0]);
          const tiempoB = parseInt(b.tiempoEntrega.split('-')[0]);
          return tiempoA - tiempoB;
        });
        break;
      case 'envio':
        resultado.sort((a, b) => a.costoEnvio - b.costoEnvio);
        break;
    }

    this.restaurantesFiltrados = resultado;
  }

  seleccionarCategoria(categoria: string): void {
    this.categoriaSeleccionada = categoria;
    this.aplicarFiltros();
  }

  onSearchChange(): void {
    this.aplicarFiltros();
  }

  onOrdenamientoChange(): void {
    this.aplicarFiltros();
  }

  verRestaurante(id: number): void {
    this.router.navigate(['/restaurante', id]);
  }
}
<div class="restaurantes-page">
  <!-- Hero Section -->
  <section class="hero-section">
    <div class="hero-content">
      <h1>Descubre Restaurantes Saludables</h1>
      <p>Los mejores lugares para comer fit en la ciudad</p>
    </div>
  </section>

  <!-- Filters and Search -->
  <section class="filters-section">
    <div class="container">
      <!-- Search Bar -->
      <div class="search-container">
        <input
          type="text"
          class="search-input"
          placeholder="Buscar restaurantes..."
          [(ngModel)]="searchTerm"
          (input)="onSearchChange()"
        />
        <span class="search-icon">üîç</span>
      </div>

      <!-- Category Filters -->
      <div class="categories-container">
        <button
          *ngFor="let categoria of categorias"
          class="category-btn"
          [class.active]="categoriaSeleccionada === categoria"
          (click)="seleccionarCategoria(categoria)"
        >
          {{ categoria }}
        </button>
      </div>

      <!-- Sort Options -->
      <div class="sort-container">
        <label>Ordenar por:</label>
        <select [(ngModel)]="ordenamiento" (change)="onOrdenamientoChange()" class="sort-select">
          <option value="calificacion">Mejor Calificaci√≥n</option>
          <option value="tiempo">Tiempo de Entrega</option>
          <option value="envio">Costo de Env√≠o</option>
        </select>
      </div>
    </div>
  </section>

  <!-- Restaurants Grid -->
  <section class="restaurants-section">
    <div class="container">
      <!-- Loading State -->
      <div *ngIf="loading" class="loading-container">
        <div class="loader"></div>
        <p>Cargando restaurantes...</p>
      </div>

      <!-- Empty State -->
      <div *ngIf="!loading && restaurantesFiltrados.length === 0" class="empty-state">
        <span class="empty-icon">üçΩÔ∏è</span>
        <h3>No se encontraron restaurantes</h3>
        <p>Intenta ajustar los filtros de b√∫squeda</p>
      </div>

      <!-- Restaurants Grid -->
      <div *ngIf="!loading && restaurantesFiltrados.length > 0" class="restaurants-grid">
        <div
          *ngFor="let restaurante of restaurantesFiltrados"
          class="restaurant-card"
          (click)="verRestaurante(restaurante.id)"
        >
          <div class="card-image">
            <img [src]="restaurante.banner" [alt]="restaurante.nombre" />
            <div class="card-badge">{{ restaurante.especialidad }}</div>
          </div>

          <div class="card-body">
            <div class="card-header">
              <img [src]="restaurante.logo" [alt]="restaurante.nombre" class="restaurant-logo" />
              <div>
                <h3>{{ restaurante.nombre }}</h3>
                <div class="rating">
                  <span class="star">‚≠ê</span>
                  <span>{{ restaurante.calificacion }}</span>
                </div>
              </div>
            </div>

            <p class="description">{{ restaurante.descripcion }}</p>

            <div class="card-footer">
              <div class="info-item">
                <span class="icon">üïê</span>
                <span>{{ restaurante.tiempoEntrega }}</span>
              </div>
              <div class="info-item">
                <span class="icon">üöö</span>
                <span>S/ {{ restaurante.costoEnvio }}</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Results Count -->
      <div *ngIf="!loading && restaurantesFiltrados.length > 0" class="results-count">
        Mostrando {{ restaurantesFiltrados.length }} de {{ restaurantes.length }} restaurantes
      </div>
    </div>
  </section>
</div>
