import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CarritoService } from '../../services/carrito.service';
import { AuthService } from '../../services/auth.service';

export interface ItemCarrito {
  id: string;
  platoId: string;
  nombre: string;
  precio: number;
  cantidad: number;
  imagen: string;
  restauranteNombre: string;
  calorias?: number;
  proteinas?: number;
}

@Component({
  selector: 'app-carrito',
  templateUrl: './carrito.component.html',
  styleUrls: ['./carrito.component.css']
})
export class CarritoComponent implements OnInit {
  items: ItemCarrito[] = [];
  loading = false;
  showLoginPrompt = false;

  constructor(
    private carritoService: CarritoService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    this.cargarCarrito();
  }

  cargarCarrito() {
    // Cargar desde localStorage
    const carritoGuardado = localStorage.getItem('carrito');
    if (carritoGuardado) {
      this.items = JSON.parse(carritoGuardado);
    }
  }

  get subtotal(): number {
    return this.items.reduce((sum, item) => sum + (item.precio * item.cantidad), 0);
  }

  get impuestos(): number {
    return this.subtotal * 0.18; // 18% IGV
  }

  get envio(): number {
    return this.subtotal > 50 ? 0 : 5.99;
  }

  get total(): number {
    return this.subtotal + this.impuestos + this.envio;
  }

  get totalItems(): number {
    return this.items.reduce((sum, item) => sum + item.cantidad, 0);
  }

  aumentarCantidad(item: ItemCarrito) {
    item.cantidad++;
    this.guardarCarrito();
  }

  disminuirCantidad(item: ItemCarrito) {
    if (item.cantidad > 1) {
      item.cantidad--;
      this.guardarCarrito();
    }
  }

  eliminarItem(index: number) {
    this.items.splice(index, 1);
    this.guardarCarrito();
  }

  vaciarCarrito() {
    if (confirm('¿Estás seguro de vaciar el carrito?')) {
      this.items = [];
      this.guardarCarrito();
    }
  }

  guardarCarrito() {
    localStorage.setItem('carrito', JSON.stringify(this.items));
  }

  procederAlPago() {
    // Verificar si está logueado
    if (this.authService.isLoggedIn()) {
      // Ir al checkout
      this.router.navigate(['/checkout']);
    } else {
      // Mostrar prompt de login
      this.showLoginPrompt = true;
    }
  }

  continuarComprando() {
    this.router.navigate(['/']);
  }

  irALogin() {
    this.router.navigate(['/login']);
  }

  cerrarPrompt() {
    this.showLoginPrompt = false;
  }
}
<div class="carrito-page">
  <div class="container">
    <!-- Header -->
    <div class="carrito-header">
      <h1>🛒 Carrito de Compras</h1>
      <button class="btn-continuar" (click)="continuarComprando()">
        ← Seguir Comprando
      </button>
    </div>

    <!-- Empty State -->
    <div class="empty-cart" *ngIf="items.length === 0">
      <div class="empty-icon">🛒</div>
      <h2>Tu carrito está vacío</h2>
      <p>¡Agrega algunas deliciosas comidas saludables para comenzar!</p>
      <button class="btn-primary" (click)="continuarComprando()">
        Explorar Restaurantes
      </button>
    </div>

    <!-- Cart Items -->
    <div class="carrito-content" *ngIf="items.length > 0">
      <div class="items-section">
        <div class="items-header">
          <h2>Artículos del Carrito ({{ totalItems }})</h2>
          <button class="btn-text" (click)="vaciarCarrito()">Vaciar Carrito</button>
        </div>

        <div class="cart-items">
          <div class="cart-item" *ngFor="let item of items; let i = index">
            <div class="item-image">
              <img [src]="item.imagen" [alt]="item.nombre">
            </div>
            
            <div class="item-details">
              <h3 class="item-name">{{ item.nombre }}</h3>
              <p class="item-restaurant">{{ item.restauranteNombre }}</p>
              <div class="item-macros" *ngIf="item.calorias">
                <span>🔥 {{ item.calorias }} cal</span>
                <span *ngIf="item.proteinas">💪 {{ item.proteinas }}g proteína</span>
              </div>
            </div>

            <div class="item-quantity">
              <button class="qty-btn" (click)="disminuirCantidad(item)">−</button>
              <span class="qty-value">{{ item.cantidad }}</span>
              <button class="qty-btn" (click)="aumentarCantidad(item)">+</button>
            </div>

            <div class="item-price">
              <div class="price-amount">${{ (item.precio * item.cantidad).toFixed(2) }}</div>
              <div class="price-unit">${{ item.precio.toFixed(2) }} c/u</div>
            </div>

            <button class="btn-remove" (click)="eliminarItem(i)" title="Eliminar artículo">
              🗑️
            </button>
          </div>
        </div>
      </div>

      <!-- Order Summary -->
      <div class="summary-section">
        <div class="summary-card">
          <h2>Resumen del Pedido</h2>
          
          <div class="summary-row">
            <span>Subtotal</span>
            <span>${{ subtotal.toFixed(2) }}</span>
          </div>
          
          <div class="summary-row">
            <span>Impuestos (18%)</span>
            <span>${{ impuestos.toFixed(2) }}</span>
          </div>
          
          <div class="summary-row">
            <span>Envío</span>
            <span *ngIf="envio > 0">${{ envio.toFixed(2) }}</span>
            <span *ngIf="envio === 0" class="free-delivery">GRATIS</span>
          </div>

          <div class="delivery-message" *ngIf="subtotal > 50">
            <span class="promo-icon">🎉</span>
            <span>¡Tienes envío gratis!</span>
          </div>
          <div class="delivery-message" *ngIf="subtotal <= 50">
            <span class="info-icon">ℹ️</span>
            <span>Agrega ${{ (50 - subtotal).toFixed(2) }} más para envío gratis</span>
          </div>

          <div class="summary-divider"></div>
          
          <div class="summary-total">
            <span>Total</span>
            <span>${{ total.toFixed(2) }}</span>
          </div>

          <button class="btn-checkout" (click)="procederAlPago()">
            Proceder al Pago
          </button>

          <div class="secure-checkout">
            <span>🔒</span>
            <span>Pago Seguro</span>
          </div>
        </div>

        <!-- Accepted Payments -->
        <div class="payment-methods">
          <p>Aceptamos</p>
          <div class="payment-icons">
            <span>💳 Tarjetas</span>
            <span>📱 Yape</span>
            <span>💰 Plin</span>
            <span>💵 Efectivo</span>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

<!-- Login Prompt Modal -->
<div class="modal-overlay" *ngIf="showLoginPrompt" (click)="cerrarPrompt()">
  <div class="modal-content" (click)="$event.stopPropagation()">
    <button class="modal-close" (click)="cerrarPrompt()">✕</button>
    <div class="modal-icon">🔐</div>
    <h2>Inicio de Sesión Requerido</h2>
    <p>Por favor inicia sesión para completar tu compra</p>
    <div class="modal-actions">
      <button class="btn-secondary" (click)="cerrarPrompt()">Cancelar</button>
      <button class="btn-primary" (click)="irALogin()">Iniciar Sesión</button>
    </div>
    <p class="modal-footer">
      ¿No tienes cuenta? <a routerLink="/register">Regístrate</a>
    </p>
  </div>
</div>
