import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { PedidoService } from '../../services/pedido.service';
import { AuthService } from '../../services/auth.service';

export interface Pedido {
  id: string;
  numeroPedido: string;
  fecha: string;
  estado: 'pendiente' | 'preparando' | 'en-camino' | 'entregado' | 'cancelado';
  items: any[];
  total: number;
  metodoPago: string;
  direccion: string;
}

@Component({
  selector: 'app-pedidos',
  templateUrl: './pedidos.component.html',
  styleUrls: ['./pedidos.component.css']
})
export class PedidosComponent implements OnInit {
  pedidos: Pedido[] = [];
  loading = true;
  filtroEstado = 'todos';
  
  estados = [
    { id: 'todos', nombre: 'Todos los Pedidos', icon: 'ðŸ“¦' },
    { id: 'pendiente', nombre: 'Pendiente', icon: 'â³' },
    { id: 'preparando', nombre: 'Preparando', icon: 'ðŸ‘¨â€ðŸ³' },
    { id: 'en-camino', nombre: 'En Camino', icon: 'ðŸšš' },
    { id: 'entregado', nombre: 'Entregado', icon: 'âœ…' },
    { id: 'cancelado', nombre: 'Cancelado', icon: 'âŒ' }
  ];

  constructor(
    private pedidoService: PedidoService,
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    if (!this.authService.isLoggedIn()) {
      this.router.navigate(['/login']);
      return;
    }
    
    this.cargarPedidos();
  }

  cargarPedidos() {
    this.pedidoService.getPedidos().subscribe({
      next: (data: any) => {
        this.pedidos = data;
        this.loading = false;
      },
      error: (err: any) => {
        console.error('Error al cargar pedidos:', err);
        this.loading = false;
        // Simular pedidos para demo
        this.cargarPedidosDemo();
      }
    });
  }

  cargarPedidosDemo() {
    // Pedidos de ejemplo para demo
    this.pedidos = [
      {
        id: '1',
        numeroPedido: '#ORD001',
        fecha: new Date().toISOString(),
        estado: 'preparando',
        items: [
          { nombre: 'Grilled Chicken Bowl', cantidad: 2, precio: 12.99 },
          { nombre: 'Quinoa Salad', cantidad: 1, precio: 9.99 }
        ],
        total: 35.97,
        metodoPago: 'yape',
        direccion: 'Av. Principal 123, Miraflores'
      },
      {
        id: '2',
        numeroPedido: '#ORD002',
        fecha: new Date(Date.now() - 86400000).toISOString(),
        estado: 'entregado',
        items: [
          { nombre: 'Teriyaki Salmon', cantidad: 1, precio: 15.99 }
        ],
        total: 18.99,
        metodoPago: 'tarjeta',
        direccion: 'Calle Los Olivos 456, San Isidro'
      }
    ];
    this.loading = false;
  }

  get pedidosFiltrados(): Pedido[] {
    if (this.filtroEstado === 'todos') {
      return this.pedidos;
    }
    return this.pedidos.filter(p => p.estado === this.filtroEstado);
  }

  filtrarPorEstado(estado: string) {
    this.filtroEstado = estado;
  }

  getEstadoClass(estado: string): string {
    const classes: any = {
      'pendiente': 'warning',
      'preparando': 'info',
      'en-camino': 'primary',
      'entregado': 'success',
      'cancelado': 'danger'
    };
    return classes[estado] || '';
  }

  getEstadoNombre(estado: string): string {
    const nombres: any = {
      'pendiente': 'Pendiente',
      'preparando': 'Preparando',
      'en-camino': 'En Camino',
      'entregado': 'Entregado',
      'cancelado': 'Cancelado'
    };
    return nombres[estado] || estado;
  }

  verDetalle(pedido: Pedido) {
    // Implementar vista de detalle
    console.log('Ver detalle del pedido:', pedido);
  }

  repetirPedido(pedido: Pedido) {
    // Agregar items al carrito
    console.log('Repetir pedido:', pedido);
    alert('Â¡ArtÃ­culos agregados al carrito!');
  }

  formatearFecha(fecha: string): string {
    const date = new Date(fecha);
    return date.toLocaleDateString('es-ES', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }
}
<div class="pedidos-page">
  <div class="container">
    <!-- Header -->
    <div class="page-header">
      <h1>ðŸ“¦ Mis Pedidos</h1>
      <p class="subtitle">Rastrea y administra tus pedidos</p>
    </div>

    <!-- Filters -->
    <div class="filters-container">
      <button 
        *ngFor="let estado of estados"
        class="filter-chip"
        [class.active]="filtroEstado === estado.id"
        (click)="filtrarPorEstado(estado.id)">
        <span class="chip-icon">{{ estado.icon }}</span>
        <span>{{ estado.nombre }}</span>
      </button>
    </div>

    <!-- Loading State -->
    <div class="loading-container" *ngIf="loading">
      <div class="spinner"></div>
      <p>Cargando pedidos...</p>
    </div>

    <!-- Empty State -->
    <div class="empty-state" *ngIf="!loading && pedidosFiltrados.length === 0">
      <div class="empty-icon">ðŸ“­</div>
      <h2>No se encontraron pedidos</h2>
      <p>AÃºn no has realizado ningÃºn pedido. Â¡Comienza a explorar nuestros restaurantes saludables!</p>
      <button class="btn-primary" routerLink="/">Explorar Restaurantes</button>
    </div>

    <!-- Orders List -->
    <div class="orders-grid" *ngIf="!loading && pedidosFiltrados.length > 0">
      <div class="order-card" *ngFor="let pedido of pedidosFiltrados">
        <!-- Order Header -->
        <div class="order-header">
          <div class="order-number">
            <span class="label">Pedido</span>
            <span class="number">{{ pedido.numeroPedido }}</span>
          </div>
          <div class="order-status" [ngClass]="getEstadoClass(pedido.estado)">
            {{ getEstadoNombre(pedido.estado) }}
          </div>
        </div>

        <!-- Order Date -->
        <div class="order-date">
          <span class="icon">ðŸ“…</span>
          <span>{{ formatearFecha(pedido.fecha) }}</span>
        </div>

        <!-- Order Items -->
        <div class="order-items">
          <div class="item-row" *ngFor="let item of pedido.items">
            <span class="item-qty">{{ item.cantidad }}x</span>
            <span class="item-name">{{ item.nombre }}</span>
            <span class="item-price">${{ item.precio.toFixed(2) }}</span>
          </div>
        </div>

        <!-- Order Details -->
        <div class="order-details">
          <div class="detail-row">
            <span class="icon">ðŸ’³</span>
            <span class="text">{{ pedido.metodoPago.toUpperCase() }}</span>
          </div>
          <div class="detail-row">
            <span class="icon">ðŸ“</span>
            <span class="text">{{ pedido.direccion }}</span>
          </div>
        </div>

        <!-- Order Footer -->
        <div class="order-footer">
          <div class="order-total">
            <span>Total:</span>
            <span class="amount">${{ pedido.total.toFixed(2) }}</span>
          </div>
          <div class="order-actions">
            <button class="btn-secondary" (click)="verDetalle(pedido)">Ver Detalles</button>
            <button class="btn-primary" (click)="repetirPedido(pedido)" *ngIf="pedido.estado === 'entregado'">
              Volver a Pedir
            </button>
          </div>
        </div>

        <!-- Progress Bar (para pedidos en proceso) -->
        <div class="order-progress" *ngIf="pedido.estado !== 'entregado' && pedido.estado !== 'cancelado'">
          <div class="progress-steps">
            <div class="progress-step" [class.active]="pedido.estado === 'pendiente' || pedido.estado === 'preparando' || pedido.estado === 'en-camino'">
              <div class="step-dot"></div>
              <div class="step-label">Confirmado</div>
            </div>
            <div class="progress-line" [class.active]="pedido.estado === 'preparando' || pedido.estado === 'en-camino'"></div>
            <div class="progress-step" [class.active]="pedido.estado === 'preparando' || pedido.estado === 'en-camino'">
              <div class="step-dot"></div>
              <div class="step-label">Preparando</div>
            </div>
            <div class="progress-line" [class.active]="pedido.estado === 'en-camino'"></div>
            <div class="progress-step" [class.active]="pedido.estado === 'en-camino'">
              <div class="step-dot"></div>
              <div class="step-label">En Camino</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
