import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService, User } from '../../services/auth.service';
import { CarritoService } from '../../services/carrito.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  currentUser: User | null = null;
  cantidadCarrito: number = 0;

  constructor(
    public authService: AuthService,
    private carritoService: CarritoService,
    private router: Router
  ) {}

  ngOnInit() {
    this.authService.currentUser$.subscribe(user => {
      this.currentUser = user;
    });

    this.carritoService.carrito$.subscribe(items => {
      this.cantidadCarrito = items.reduce((total, item) => total + item.cantidad, 0);
    });
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/']);
  }
}
<nav class="navbar">
  <div class="container nav-container">
    <a routerLink="/" class="logo">
      ü•ó <span>PrimeFit</span>
    </a>

    <div class="nav-links">
      <a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Inicio</a>
      <a routerLink="/restaurantes" routerLinkActive="active">Restaurantes</a>
      
      <div class="nav-right">
        <a routerLink="/carrito" class="cart-link">
          üõí Carrito
          <span class="cart-badge" *ngIf="cantidadCarrito > 0">{{ cantidadCarrito }}</span>
        </a>

        <ng-container *ngIf="currentUser; else loginButton">
          <a routerLink="/pedidos" routerLinkActive="active">Mis Pedidos</a>
          <a *ngIf="currentUser.role === 'admin'" routerLink="/admin" routerLinkActive="active" class="admin-link">
            üõ†Ô∏è Admin
          </a>
          <div class="user-menu">
            <span class="user-name">{{ currentUser.nombre }}</span>
            <button (click)="logout()" class="btn btn-outline">Cerrar Sesi√≥n</button>
          </div>
        </ng-container>

        <ng-template #loginButton>
          <a routerLink="/login" class="btn btn-primary">Iniciar Sesi√≥n</a>
        </ng-template>
      </div>
    </div>
  </div>
</nav>
