import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { RestauranteService, Restaurante } from '../../services/restaurante.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  restaurantes: Restaurante[] = [];
  restaurantesFiltrados: Restaurante[] = [];
  loading = true;
  searchTerm = '';
  categoriaSeleccionada = '';

  categorias = [
    { id: 'vegano', nombre: 'Vegano', icon: 'üå±' },
    { id: 'proteina', nombre: 'Alto en Prote√≠na', icon: 'üí™' },
    { id: 'keto', nombre: 'Keto', icon: 'ü•ë' },
    { id: 'balanceado', nombre: 'Balanceado', icon: '‚öñÔ∏è' },
    { id: 'paleo', nombre: 'Paleo', icon: 'ü•©' },
    { id: 'mediterraneo', nombre: 'Mediterr√°neo', icon: 'ü´í' }
  ];

  constructor(
    private restauranteService: RestauranteService,
    private router: Router
  ) {}

  ngOnInit() {
    this.cargarRestaurantes();
  }

  cargarRestaurantes() {
    this.restauranteService.getRestaurantes().subscribe({
      next: (data: Restaurante[]) => {
        this.restaurantes = data;
        this.restaurantesFiltrados = data;
        this.loading = false;
      },
      error: (error: any) => {
        console.error('Error al cargar restaurantes:', error);
        this.loading = false;
      }
    });
  }

  buscarRestaurantes() {
    this.filtrarRestaurantes();
  }

  filtrarPorCategoria(categoria: string) {
    this.categoriaSeleccionada = this.categoriaSeleccionada === categoria ? '' : categoria;
    this.filtrarRestaurantes();
  }

  filtrarRestaurantes() {
    let resultados = this.restaurantes;

    // Filtrar por b√∫squeda
    if (this.searchTerm) {
      const term = this.searchTerm.toLowerCase();
      resultados = resultados.filter(r => 
        r.nombre.toLowerCase().includes(term) ||
        r.descripcion.toLowerCase().includes(term)
      );
    }

    // Filtrar por categor√≠a
    if (this.categoriaSeleccionada) {
      resultados = resultados.filter(r => 
        r.categorias?.some(c => c.toLowerCase().includes(this.categoriaSeleccionada.toLowerCase()))
      );
    }

    this.restaurantesFiltrados = resultados;
  }

  verRestaurante(id: string) {
    this.router.navigate(['/restaurante', id]);
  }
}
<div class="home">
  <!-- Hero Section -->
  <section class="hero">
    <div class="hero-overlay"></div>
    <div class="hero-content">
      <h1 class="hero-title">Discover Healthy Restaurants</h1>
      <p class="hero-subtitle">Explore a variety of nutritious meals from top-rated restaurants</p>
      
      <!-- Search Bar -->
      <div class="search-container">
        <div class="search-box">
          <span class="search-icon">üîç</span>
          <input 
            type="text" 
            [(ngModel)]="searchTerm" 
            (input)="buscarRestaurantes()"
            placeholder="Search restaurants, cuisines, dishes..."
            class="search-input">
        </div>
      </div>

      <!-- Promo Banner -->
      <div class="promo-banner">
        <span class="promo-text">20% Off All Salads</span>
        <button class="promo-btn">Order Now</button>
      </div>
    </div>
  </section>

  <!-- Categories -->
  <section class="categories">
    <div class="container">
      <div class="categories-scroll">
        <button 
          *ngFor="let cat of categorias"
          class="category-chip"
          [class.active]="categoriaSeleccionada === cat.id"
          (click)="filtrarPorCategoria(cat.id)">
          <span class="category-icon">{{cat.icon}}</span>
          <span class="category-name">{{cat.nombre}}</span>
        </button>
      </div>
    </div>
  </section>

  <!-- Restaurants Section -->
  <section class="restaurants">
    <div class="container">
      <h2 class="section-title">Restaurants</h2>
      
      <div class="loading-spinner" *ngIf="loading">
        <div class="spinner"></div>
      </div>
      
      <div class="restaurants-grid" *ngIf="!loading">
        <div 
          *ngFor="let restaurante of restaurantesFiltrados" 
          class="restaurant-card"
          (click)="verRestaurante(restaurante.id)">
          <div class="restaurant-image">
            <img [src]="restaurante.banner" [alt]="restaurante.nombre">
            <div class="restaurant-badge" *ngIf="restaurante.envioGratis">
              <span>üöö Free Delivery</span>
            </div>
          </div>
          <div class="restaurant-content">
            <div class="restaurant-header">
              <img [src]="restaurante.logo" [alt]="restaurante.nombre" class="restaurant-logo">
              <h3 class="restaurant-name">{{ restaurante.nombre }}</h3>
            </div>
            <p class="restaurant-description">{{ restaurante.descripcion }}</p>
            <div class="restaurant-tags">
              <span class="tag" *ngFor="let cat of restaurante.categorias?.slice(0, 3)">{{cat}}</span>
            </div>
            <div class="restaurant-meta">
              <span class="meta-item">‚≠ê {{ restaurante.calificacion }}</span>
              <span class="meta-item">üïí {{ restaurante.tiempoEntrega }} min</span>
              <span class="meta-item">üíµ ${{ restaurante.costoEnvio?.toFixed(2) }}</span>
            </div>
            <button class="view-menu-btn">View Menu</button>
          </div>
        </div>
      </div>

      <!-- Empty State -->
      <div class="empty-state" *ngIf="!loading && restaurantesFiltrados.length === 0">
